echo "/* Estilos para filas de formulario en modal */
.Modal-form-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
}

/* Asegurar que los select tengan el mismo estilo que los inputs */
.Modal-form select {
  border: 1px solid #D0D5D9;
  border-radius: 12px;
  padding: .8rem 1rem;
  font-size: .95rem;
  color: var(--dark);
  transition: border-color .2s;
  background-color: #fff;
  width: 100%;
  appearance: none; /* Eliminar estilo nativo para consistencia */
  background-image: url(\"data:image/svg+xml;charset=UTF-8,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='currentColor' stroke-width='2' stroke-linecap='round' stroke-linejoin='round'%3e%3cpolyline points='6 9 12 15 18 9'%3e%3c/polyline%3e%3c/svg%3e\");
  background-repeat: no-repeat;
  background-position: right 1rem center;
  background-size: 1em;
}

.Modal-form select:focus {
  border-color: var(--brand);
  outline: none;
}" >> "c:\Users\SANDRO\VS Code\Programacion Web\Proyecto-NutriTrack\primer-proyecto\css\main.css"

echo "
/* Resumen Dashboard Widget */
.Resumen-stats {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1rem;
  margin-bottom: 1.2rem;
  text-align: center;
}
.Resumen-stat {
  display: flex;
  flex-direction: column;
  gap: 0.2rem;
  padding: 0.8rem 0.5rem;
  background: var(--light);
  border-radius: 12px;
  border: 1px solid var(--border);
}
.Resumen-label {
  font-size: 0.8rem;
  color: var(--muted);
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}
.Resumen-value {
  font-size: 1.25rem;
  font-weight: 800;
  color: var(--dark);
}
.Resumen-value.is-danger { color: var(--danger); }
.Resumen-value.is-success { color: var(--brand); }
" >> "c:\Users\SANDRO\VS Code\Programacion Web\Proyecto-NutriTrack\primer-proyecto\css\main.css"
